package com.common;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 *
 * @author dungptm2
 */
public class FindAnagrams {
    
    public static FindAnagrams INSTANCE = new FindAnagrams();
    
    //s = "cbaebabacd"
    //p = "abc"
    //[0,6]
    
    public List<Integer> findAnagrams2(String s, String p) {
        List<Integer> result = new ArrayList<>();
        int pLen = p.length();
        int sLen = s.length();
        
        Map<Integer, Integer> mapP = new HashMap<>(pLen);
        char[] pChars = p.toCharArray();
        for (char pChar : pChars) {
            mapP.put(pChar - 'a', mapP.getOrDefault(pChar - 'a', 0) + 1);
        }
        
        int count = 0;
        
        char[] sChars = s.toCharArray();
        Map<Integer, Integer> mapPFound = new HashMap<>();
        for (int i = 0; i < sLen; i++) {
            count ++;
            
            if (sChars[i] == 'b') {
                int a = 1;
            }
            
            if (mapP.getOrDefault(sChars[i] - 'a', 0) != 0) {
                mapPFound.put(sChars[i] - 'a', mapPFound.getOrDefault(sChars[i] - 'a', 0) + 1);
            } else {
                int descCount = 0;
                for (int j = 1; j < count; j++) {
                    mapPFound.put(sChars[i-j] - 'a', mapPFound.get(sChars[i-j] - 'a') - 1);
                    descCount++;
                }
                count = count - descCount - 1;
            }
            
            if (count == pLen) {
                if (mapP.size() == mapPFound.size()) {
                    boolean correct = true;
                    for (Map.Entry<Integer, Integer> entry : mapP.entrySet()) {
                        if (entry.getValue().intValue() != mapPFound.getOrDefault(entry.getKey(), 0).intValue()) {
                            correct = false;
                            break;
                        }
                    }
                    if (correct) {
                        result.add(i-count + 1);
                    }
                    
                } 
                
                mapPFound.put(sChars[i - count + 1] - 'a', mapPFound.get(sChars[i - count + 1] - 'a') - 1);
                count--;
            }
        }
        
        return result;
    }
    
    // cach 2
    //s = "cbaebabacd"
    //p = "abc"
    //[0,6]
    public List<Integer> findAnagrams(String s, String p) {
        List<Integer> result = new ArrayList<>();
        int pLen = p.length();
        int sLen = s.length();
        
        int[] sArray = new int [26];
        int[] pArray = new int[26];
        
        for (int i = 0; i< pLen; i++) {
            pArray[p.charAt(i) - 'a']++;
            sArray[s.charAt(i) - 'a']++;
        }
        
        int i = 0;
        while (i + pLen <= sLen) {
            if (compare2List(pArray, sArray)) {
                result.add(i);
            }
            
            sArray[s.charAt(i) - 'a']--;
            sArray[s.charAt(i + pLen) - 'a']++;
            i++;
        }
        
        
        return result;
    }
    
    private boolean compare2List(int[] a, int[] b) {
        for (int i = 0; i < 26; i++) {
            if (a[i] != b[i]) {
                return false;
            }
        }
        
        return true;
    }
    
    public static void main(String[] args) {
        String s = "";
        String p = "";
        
        List<Integer> ret = INSTANCE.findAnagrams(s, p);
        System.out.println("ret = " + ret);
        
        System.exit(0);
    }
    
}
